import React from 'react';
import fetch from 'node-fetch';
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from 'react-apollo';
import { Redirect } from 'react-router-dom';
import LoginView from '../src/pages/Login/LoginView';
import { useRouter } from 'next/router'

import "semantic-ui-css/semantic.min.css";
import "../src/styles/App.css";


function handleSuccessfulLogin(router) {
  return function (loginResult) {
    document.cookies.set(ctx,'GofarDashboardUserId',loginResult.userId,{
        maxAge: 30 * 24 * 60 * 60,
        path: '/',
      });
    document.cookies.set(ctx,'GofarDashboardToken',loginResult.userToken,{
        maxAge: 30 * 24 * 60 * 60,
        path: '/',
    });
    router.push('/dashboard');
  }
}

export default function LoginPage(props) {
  if (props.authToken) {
    return <Redirect to="/" />;
  }

  const router = useRouter();

  const client = new ApolloClient({
    uri:
      'https://jt63wdhqqre6rpjo5lnylfhjqm.appsync-api.ap-southeast-2.amazonaws.com/graphql',
    headers: {
      Authorization: props.authToken,
      'X-API-KEY': 'da2-d5trhzrnqraznclr2teue2bmmu',
    },
    fetch
  });

  return (
    <ApolloProvider client={client}>
      <LoginView {...props} handleSuccessfulLogin={handleSuccessfulLogin(router)} />
    </ApolloProvider>
  );
}

LoginPage.getInitialProps = (ctx) => {
  console.log(ctx.req.cookie);
  return {
    cookies: ctx.req.cookie
  }
}
